function genQuote(){$.getJSON("http://api.forismatic.com/api/1.0/?method=getQuote&format=jsonp&lang=en&jsonp=?",function(t){$(".quote").text(t.quoteText),$(".author").text(t.quoteAuthor);var e="https://twitter.com/intent/tweet?text="+t.quoteText+" - "+t.quoteAuthor;$("#tweet").on("click",function(){$(this).attr("href",e)})})}$(document).ready(function(){genQuote(),$("#generateQuote").on("click",function(){genQuote()})});